#include <bits/stdc++.h>
using namespace std;
/**
 * 三数之和
 * 本题目为三个数字，因此肯定是离不开循环的
 * 具体来说
 * 1. 首先通过排序算法，使得给出的数组成为一个排序的数组
 * 2. 开始外层的第一层遍历，得到一个数字，那么剩下的两个数字只要和满足条件即可
 * 3. 搜索两个数字，并且是一个排序数字，因此使用双指针即可
 * 4. 因为不能出现重复的，因此需要添加一个防止重复的判断
 */
vector<vector<int>> threeSum(vector<int> &nums)
{
    vector<vector<int>> res;
    // vector<vector<int>> ans;

    // 首先对数组进行排序。方便操作
    sort(nums.begin(), nums.end());

    int size = nums.size();
    if (size <= 2)
        return res;

    for (int i = 0; i < size; i++)
    {
        // 因为可能出现重复的数组，因此取消一个排序
        if (i > 0 && nums[i] == nums[i - 1])
            continue;

        int target = 0 - nums[i];
        // 下面开始寻找这个target
        int left = i + 1;
        int right = size - 1;
        while (left < right)
        {
            // 添加和上次出现的一样的判断
            if (left > i + 1 && nums[left] == nums[left - 1])
            {
                left++;
                continue;
            }
            if (right < size - 1 && nums[right] == nums[right + 1])
            {
                right--;
                continue;
            }
            if (nums[left] + nums[right] == target)
            {
                vector<int> temp;
                temp.push_back(nums[i]);
                temp.push_back(nums[left]);
                temp.push_back(nums[right]);
                res.push_back(temp);

                left++;
                right--;
            }
            else if (nums[left] + nums[right] > target)
            {
                right--;
            }
            else
            {
                left++;
            }
        }
    }

    // for (auto &i : res)
    // {
    //     ans.push_back(i);
    // }
    return res;
}
